    my $ssh = Net::SSH::Perl->new($host, port => $port, user => $username, password => $password);
    $ssh->error and die "SSH error: " . $ssh->error;

    my $output = $ssh->cmd($command);
    $output =~ s/[\r\n]//g;

    print "Command executed: $command\n";
    print "Output: $output\n";

    $ssh->disconnect;
}

# Main
send_ssh_command($target_ip, $port, $username, $password, $command);

